/////////////////////////
// TYPES
/////////////////////////

export type Layer = keyof typeof INSCRIBED_ATTRIBUTES;

export type LayerSelection = {
  [key: string]: string | undefined;
};

/////////////////////////
// HELPER FUNCTIONS
/////////////////////////

export function selectLayers(hashArray: number[], onchain = false) {
  const selectedLayers: LayerSelection = {};
  let hashIndex = 0;
  for (const [key, value] of Object.entries(
    onchain ? INSCRIBED_ATTRIBUTES : LOCAL_ATTRIBUTES
  )) {
    const index = hashArray[hashIndex % hashArray.length] % value.length;
    const chosenValue = value[index];
    selectedLayers[key] = chosenValue;
    hashIndex++;
  }
  return selectedLayers;
}

export function listLayers(hashArray: number[], onchain = false) {
  const listedLayers: string[] = [];
  let hashIndex = 0;
  for (const [key, value] of Object.entries(
    onchain ? INSCRIBED_ATTRIBUTES : LOCAL_ATTRIBUTES
  )) {
    const index = hashArray[hashIndex % hashArray.length] % value.length;
    listedLayers.push(`${key}-${index + 1}`);
    hashIndex++;
  }
  return listedLayers;
}

export function getLayers(
  layers: LayerSelection,
  onchain = false,
  host = "/content"
) {
  const result: { [key: string]: string } = {};
  Object.entries(layers)
    .filter(([key, value]) => value !== undefined)
    .forEach(([key, value], index) => {
      if (onchain) {
        result[key] = `<image id="${key}-${
          index + 1
        }" xlink:href="${host}/${value}" x="0" y="0" width="500" height="500"></image>`;
      } else {
        result[key] = value as string;
      }
    });
  return result;
}

export function createLayers(
  layers: LayerSelection,
  onchain = false,
  host = "/content"
) {
  return Object.entries(layers)
    .filter(([key, value]) => value !== undefined)
    .map(([key, value], index) => {
      if (onchain) {
        // return a link to the on-chain image hash
        return `<image id="${key}-${
          index + 1
        }" xlink:href="${host}/${value}" x="0" y="0" width="500" height="500"></image>`;
      }
      // return the matching layer in the cache
      return value;
    })
    .join("\n");
}

export function createSvgFile(
  name: string,
  svgLayers: string,
  width = 500,
  height = 500
) {
  // TODO: add flag for laser eyes, add defs
  return `<svg id="Bitcoin Face for ${name}" width="${width}" height="${height}" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">\n${svgLayers}\n</svg>`;
}
